import pygame
import time

win = pygame.display.set_mode((750, 750))
pygame.display.set_caption("MAPS")

clock = pygame.time.Clock()

levels = []
levelsave = []
currentlevel = 0

with open('levels.txt', 'r') as data:
	for line in data:
		if len(levelsave) >= 25:	
			levels.append(levelsave)
			levelsave = []
			print('level')

		if len(levelsave) < 25:
			levelrow = line.split()
			levelrow = [int(i) for i in levelrow]
			levelsave.append(levelrow)
			print('row')
	levels.append(levelsave)
	levelsave = []

print(levels, len(levels))

