mainloop = True
while mainloop:
	printX = 0
	printY = 0

	sqW = 30
	sqG = 0

	playerW = 26
	playerG = 2
	move = 0

	level = levels[currentlevel]

	printX = 0
	printY = 0
	for row in level:
		for sq in row:
			if sq == 2:
				playerX = printX
				playerY = printY
			printX += 1
		printY += 1
		printX = 0

	printX = 0
	printY = 0
	for row in level:
		for sq in row:
			if sq == 3:
				targX = printX
				targY = printY
			printX += 1
		printY += 1
		printX = 0

	def draw():
		pygame.draw.rect(win, (0, 0, 0), (0,0,750,750))
		printX = 0
		printY = 0
		for row in level:
			for sq in row:
				if sq == 0:
					pygame.draw.rect(win, (255, 255, 255), (printX * 30 + sqG, printY * 30 + sqG, sqW, sqW))
				if sq == 1:
					pygame.draw.rect(win, (0, 0, 0), (printX * 30 + sqG, printY * 30 + sqG, sqW, sqW))
				if sq == 2:
					pygame.draw.rect(win, (255, 0, 0), (printX * 30 + sqG, printY * 30 + sqG, sqW, sqW))
				if sq == 3:
					pygame.draw.rect(win, (0, 255, 0), (printX * 30 + sqG, printY * 30 + sqG, sqW, sqW))
				

				printX += 1
			printY += 1
			printX = 0

		pygame.draw.rect(win, (0, 0, 255), (playerX * 30 + playerG, playerY * 30 + playerG, playerW, playerW))

		pygame.display.update()

	loop = True
	while loop:
		clock.tick(60)
		
		#exit
		for event in pygame.event.get():
			if event.type == pygame.QUIT:
				pygame.quit()
		
		keys = pygame.key.get_pressed()

		if keys[pygame.K_UP] and move == 0:
			move = 1

		if keys[pygame.K_DOWN] and move == 0:
			move = 2

		if keys[pygame.K_LEFT] and move == 0:
			move = 3

		if keys[pygame.K_RIGHT] and move == 0:
			move = 4

		if keys[pygame.K_SPACE]:
				printX = 0
				printY = 0
				for row in level:
					for sq in row:
						if sq == 2:
							playerX = printX
							playerY = printY
						printX += 1
					printY += 1
					printX = 0

		if move == 1 and (level[playerY - 1][playerX] == 0 or level[playerY - 1][playerX] == 2 or level[playerY - 1][playerX] == 3):
			playerY -= 1
		elif move == 1 and level[playerY - 1][playerX] == 1:
			move = 0

		if move == 2 and (level[playerY + 1][playerX] == 0 or level[playerY + 1][playerX] == 2 or level[playerY + 1][playerX] == 3):
			playerY += 1
		elif move == 2 and level[playerY + 1][playerX] == 1:
			move = 0

		if move == 3 and (level[playerY][playerX - 1] == 0 or level[playerY][playerX - 1] == 2 or level[playerY][playerX - 1] == 3):
			playerX -= 1
		elif move == 3 and level[playerY][playerX - 1] == 1:
			move = 0

		if move == 4 and (level[playerY][playerX + 1] == 0 or level[playerY][playerX + 1] == 2 or level[playerY][playerX + 1] == 3):
			playerX += 1
		elif move == 4 and level[playerY][playerX + 1] == 1:
			move = 0

		if playerX == targX and playerY == targY and move == 0:
			loop = False
			currentlevel += 1
			time.sleep(0.5)

		draw()
